import React from "react";
import { useState, useEffect } from "react";
import MyEditor from "./MyEditor";
import { Editor } from "@tinymce/tinymce-react";
import { useRef } from "react";
export default function Card() {
  const [text, setText] = useState("hello");
  const editorRef = useRef();
  useEffect(() => {
    const saveuserdata = localStorage.getItem("user_text");
    if (saveuserdata) {
      setText(saveuserdata);
    }
  }, []);

  const handleEditorChange = (content, editor) => {
    setText(content);
    localStorage.setItem("user_text", content);
    console.log(content);
    alert("hello");
  };
  return (
    <div>
      <div
        className="container p-3 "
        style={{ height: "800px", margin: "10px" }}
      >
        <form id="post">
          <div
            className="container p-3"
            style={{ height: "800px", margin: "10px" }}
          >
            <form id="post">
              {/* Use TinyMCE Editor component with onEditorChange event */}
              <Editor
                apiKey="your-api-key" // Replace with your TinyMCE API key
                initialValue={text}
                init={{
                  height: 500,
                  menubar: false,
                  plugins: [
                    "advlist autolink lists link image charmap print preview anchor",
                    "searchreplace visualblocks code fullscreen",
                    "insertdatetime media table paste code help wordcount",
                  ],
                  toolbar:
                    "undo redo | formatselect | " +
                    "bold italic backcolor | alignleft aligncenter " +
                    "alignright alignjustify | bullist numlist outdent indent | " +
                    "removeformat | help",
                }}
                onEditorChange={handleEditorChange}
              />
            </form>
          </div>
        </form>
        {/* </div> */}
      </div>
    </div>
  );
}
