import React from "react";
import { useState } from "react";
import MyEditor from "./MyEditor";
import { Editor } from "@tinymce/tinymce-react";
import { useRef } from "react";
export default function Card() {
  const editorRef = useRef();
  return (
    <div>
      <div
        className="container p-3 "
        style={{ height: "800px", margin: "10px" }}
      >
        {/* <div className="card rounded-5 h-100"> */}
        {/* <textarea
            className="form-control h-100 rounded-5"
            id="exampleFormControlTextarea1"
            rows="3"
            style={{ resize: "none", border: "transparent" }}
          ></textarea> */}
        <form id="post">
          <textarea
            id="editor"
            onChange={(event) => {
              localStorage.setItem("user_text", event.target.value);
            }}
          >
            {localStorage.getItem("user_text")}
          </textarea>
        </form>
        {/* </div> */}
      </div>
    </div>
  );
}
